		<p class="info">
			<span class="date">{$newsPage->getDate()|date_format:"%d. %B %Y"}</span> 
                        <span class="user">{news_category item=$newsPage suffix="," last=" | "}
		{user id=$newsPage->getCreateByID()} </span>
	{configuration package="CrystalX.design" key="Use.Comments" default=false assign="CommentsEnable"}
	{if $CommentsEnable}
		{hooks_has_action name="comments_plugin" assign="commentsInstalled"}
			{if $commentsInstalled}{project_field item=$newsPage type="num" name="allow_comments" assign="allow_comments" default=true}{if $allow_comments}{load_translation name="comments-form"}
				<span class="comments"><a href="{link item=$newsPage}#comments">{comment_counter item=$newsPage} {translate key="comment.formular.title"}</a></span>
			{/if}{/if}{/if}
		</p>
		<p>{$newsPage->getTeaser()}</p>
		<p class="btn-more noprint"><a href="{link item=$newsPage}">Read more:{* &raquo;{$newsPage->getTitle()}&laquo;*}</a></p>
	  </div>
